import React, { useState } from 'react';
import { Snackbar } from '@material-ui/core'
import { Alert } from '@material-ui/lab'

export default function CredentialsContainer() {
    const [snackbar, setSnacbar] = useState();
    const [open, setOpen] = useState(true);

    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }

        setOpen(false);
    };

    const submit = (e) => {
        e.preventDefault();

        const form = document.getElementById('credentials');
        const formData = new FormData(form);

        var object = {};
        formData.forEach(function (value, key) {
            object[key] = value;
        });

        const requestOptions = {
            method: 'PUT',
            body: JSON.stringify(object),
            headers: {
                'Content-Type': 'application/json'
            }
        }

        fetch('http://localhost:8080/credential', requestOptions)
            .then(response)
            .then(function (data) {
                setSnacbar(
                    <Snackbar
                        anchorOrigin={{vertical:'Top', horizontal:'Center'}}
                        open={open} autoHideDuration={6000} onClose={handleClose}>
                        <Alert variant='filled' onClose={handleClose} severity="success">
                            Credentials added successfully
                        </Alert>
                    </Snackbar>
                )
            })
            .catch(function (error) {
                console.log(error);

                setSnacbar(
                    <Snackbar
                        anchorOrigin={{vertical:'Top', horizontal:'Center'}}
                        open={open} autoHideDuration={6000} onClose={handleClose}>
                        <Alert variant='filled' onClose={handleClose} severity="error">
                            {error + ''}
                        </Alert>
                    </Snackbar>
                )
            });
    }

    return (
        <React.Fragment>
            {snackbar}
            <form onSubmit={(e) => submit(e)} id='credentials'>
                <label>Key</label>
                <input name='key' required />
                <label>Shared Secret</label>
                <input name='shared_secret' required />
                <button type='submit'>Add Credentials</button>
            </form>
        </React.Fragment>
    );
}
